$('#height').click(function () {
  $('#rectangle').css('height', '260px');
});

$('#green').click(function () {
  $('#rectangle').css('background-color', 'green');
});

$('#resetColor').click(function () {
  $('#rectangle').css('background-color', 'yellow');
});

$('#hide').click(function () {
  $('#rectangle').hide();
});

$('#show').click(function () {
  $('#rectangle').show();
});